function updateScreenAdmin()
    local adminPrimaryColor = "F00" --export
    local adminCircuitColorA = "5008" --export
    local adminCircuitColorB = "4008" --export
    local adminCircuitColorC = "F008" --export

    local playersHtml = [[
        <text x='30' y='16' width='100%' font-size='0.8em' text-anchor='middle' role="row">
            <tspan x='15' font-weight='bold' text-anchor='start'>ID</tspan>
            <tspan x='55' text-anchor='start'>Nom</tspan>
            <tspan x='180'>Date</tspan>
        </text>
    ]]

    local logPlayers = json.decode(databank.getStringValue('logPlayers'))
    local dy = 1
    for key, logPlayer in ipairs(logPlayers) do
        local isAutorizePlayer = 'crimson'
        if logPlayer.isAuthorize then
            isAutorizePlayer = 'green'
        end
        playersHtml = playersHtml .. [[
            <text x='30' y='20' width='210' font-size='0.8em' text-anchor='middle' role="row">
                <tspan x='15' dy=']]..tostring(dy)..[[em' font-weight='bold' fill=']]..isAutorizePlayer..[[' text-anchor='start'>]]..logPlayer.id..[[</tspan>
                <tspan x='55' text-anchor='start'>]]..logPlayer.name..[[</tspan>
                <tspan x='180'>]]..epochTime(logPlayer.time)..[[</tspan>
            </text>
        ]]
        dy = dy + 1
    end

    local windowContent = [[
        <g id='rowGroup' transform='translate(0, 0)'>
            ]]..playersHtml..[[
        </g> 
    ]]

    if adminPrimaryColor == nil then
        adminPrimaryColor = 'F00'
    end
    if adminCircuitColorA == nil then
        adminCircuitColorA = '5008'
    end
    if adminCircuitColorB == nil then
        adminCircuitColorB = '4008'
    end
    if adminCircuitColorC == nil then
        adminCircuitColorC = 'F008'
    end

    local html = [[
        <style>
            :root {
                --primary-color: #]]..adminPrimaryColor..[[;
                --highlight-color: #fff;
                --text-color: #FFF;


                --circuit-color-A: #]]..adminCircuitColorA..[[;
                --circuit-color-B: #]]..adminCircuitColorB..[[;
                --circuit-color-C: #]]..adminCircuitColorC..[[;  
            }     
        </style>

        <style>  
            * {
                margin: 0;
                box-sizing: border-box;
                padding: 0;
            }

            .main {
                width: 10wv;
                height: 20vh;
                position: absolute;
                left: 78vw;
                top: 29.5vh;
                -webkit-transform: translate(-50%, -50%);
                transform: translate(-50%, -50%);
                font-size: 15px;
                -webkit-perspective: 2560px;
                perspective: 2560px;
            }

            #rowGroup {
                fill: white;
            }
        </style>
        ]]..getScreenBG()..[[  
        ]]..getScreenWindow(windowContent)..[[
    ]]
    screens[2].setHTML(html)
end

